def min_rounds(start, end, positions):
    memo = {}

    def solve(current, target):
        if (current, target) in memo:
            return memo[(current, target)]

        if current == target:
            return 0

        if current not in positions:
            return float('inf')

        min_rounds = float('inf')
        for option in positions[current]:
            if option == target:
                min_rounds = 1
                break
            for next_pos in positions[option]:
                if next_pos in positions:
                    rounds = 1 + solve(next_pos, target)
                    min_rounds = min(min_rounds, rounds)

        memo[(current, target)] = min_rounds
        return min_rounds

    rounds = solve(start, end)
    if rounds == float('inf'):
        return -1
    else:
        return rounds

