            spread += math.sqrt((point[0] - grip_points[0][0])**2 + (point[1] - grip_points[0][1])**2)
        return spread / len(grip_points)

    def calculate_grip_rotation(touches1, touches2, grip1, grip2):
        rotation = 0
        for i in range(len(touches1)):
            touch1_x, touch1_y = touches1[i]
            touch2_x, touch2_y = touches2[i]

            vector1 = (touch1_x - grip1[0], touch1_y - grip1[1])
            vector2 = (touch2_x - grip2[0], touch2_y - grip2[1])

            dot_product = vector1[0] * vector2[0] + vector1[1] * vector2[1]
            magnitude1 = math.sqrt(vector1[0]**2 + vector1[1]**2)
            magnitude2 = math.sqrt(vector2[0]**2 + vector2[1]**2)

            angle = math.acos(dot_product / (magnitude1 * magnitude2))

            if vector1[1] * vector2[0] - vector1[0] * vector2[1] < 0:
                angle = -angle

            rotation += angle
        return rotation / len(touches1)

    grip1 = calculate_grip(touches1)
    grip2 = calculate_grip(touches2)

    grip_spread1 = calculate_grip_spread(touches1)
    grip_spread2 = calculate_grip_spread(touches2)

    grip_rotation = calculate_grip_rotation(touches1, touches2, grip1, grip2)

    pan_distance = math.sqrt((grip2[0] - grip1[0])**2 + (grip2[1] - grip1[1])**2)
    zoom_distance = grip_spread2 - grip_spread1
    rotation_distance = abs(grip_rotation) * grip_spread1

    if pan_distance >= zoom_distance and pan_distance >= rotation_distance:
        print(f"{len(touches1)} pan")
    elif zoom_distance >= pan_distance and zoom_distance >= rotation_distance:
        zoom_direction = "out" if grip_spread2 < grip_spread1 else "in"
        print(f"{len(touches1)} zoom {zoom_direction}")
    else:
        rotation_direction = "clockwise" if grip_rotation > 0 else "counter-clockwise"
        print(f"{len(touches1)} rotate {rotation_direction}")

solve()

