def solve():
    n = int(input())
    nums = []
    for _ in range(n):
        nums.append(int(input()))

    def gcd(a, b):
        if b == 0:
            return a
        return gcd(b, a % b)

    def lcm(a, b):
        return (a * b) // gcd(a, b)

    def find_lcm(arr):
        result = arr[0]
        for i in range(1, len(arr)):
            result = lcm(result, arr[i])
        return result

    lcm_val = find_lcm(nums)

    if lcm_val > 10**9:
        print(0)
        return

    def count_ways(arr):
        n = len(arr)
        dp = {}

        def solve(index, prev_lcm):
            if index == n:
                return 1

            if (index, prev_lcm) in dp:
                return dp[(index, prev_lcm)]

            ways = 0
            for i in range(n):
                if i != index:
                    new_lcm = lcm(prev_lcm, arr[i])
                    if new_lcm > 10**9:
                        continue
                    ways = (ways + solve(i, new_lcm)) % (10**9 + 7)

            dp[(index, prev_lcm)] = ways
            return ways

        return solve(0, 1)

    print(count_ways(nums))

