    def is_valid(x, y):
        return 0 <= x < r and 0 <= y < c and grid[x][y] == '.'

    def bfs(start, masters):
        q = [(start, 0)]
        visited = {tuple(start)}

        while q:
            (x, y), turns = q.pop(0)

            if (x, y) == fridge:
                return turns

            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = x + dx, y + dy
                if is_valid(nx, ny) and (nx, ny) not in visited:
                    visited.add((nx, ny))
                    q.append(((nx, ny), turns + 1))

            # Move masters
            for master in masters:
                mx, my = master[0], master[1]
                if master == masters[-1]:
                    master[0] = masters[0][0]
                    master[1] = masters[0][1]
                else:
                    master[0] = masters[masters.index(master) + 1][0]
                    master[1] = masters[masters.index(master) + 1][1]

                if is_valid(mx, my) and (mx, my) not in visited:
                    visited.add((mx, my))
                    q.append(((mx, my), turns + 1))

