def preprocess_line(line):
    line = line.strip()
    if not line:
        return ""

    words = []
    i = 0
    while i < len(line):
        if line[i].isspace():
            i += 1
            continue
        start = i
        while i < len(line) and not line[i].isspace():
            i += 1
        words.append(line[start:i])
    return " ".join(words)

