def solve():
    s = input()
    n = len(s)

    def calculate_weight(factored_string):
        weight = 0
        i = 0
        while i < len(factored_string):
            if factored_string[i] == '(':
                j = i
                while factored_string[j] != ')':
                    j += 1
                exponent_start = j + 1
                exponent_end = j + 1
                while exponent_end < len(factored_string) and factored_string[exponent_end].isdigit():
                    exponent_end += 1

                exponent = int(factored_string[exponent_start:exponent_end])

                weight += (exponent - 1) * (j - i - 1)

                i = exponent_end
            else:
                weight += 1
                i += 1
        return weight

    def find_maximal_factoring(s):
        n = len(s)
        if n == 0:
            return ""

        best_factored_string = ""

        for i in range(1, n + 1):
            if n % i == 0:
                factor_length = n // i
                factor = s[:factor_length]

                factored_string = factor
                for j in range(1, n // factor_length):
                    factored_string += "(" + factor + str(j) + ")"

                weight = calculate_weight(factored_string)

                if best_factored_string == "" or weight < calculate_weight(best_factored_string):
                    best_factored_string = factored_string

        return best_factored_string

    factored_string = find_maximal_factoring(s)
    print(calculate_weight(factored_string))

solve()

