def solve():
    n, m = map(int, input().split())
    roads = []
    for _ in range(m):
        roads.append(list(map(int, input().split())))

    def dijkstra(start):
        dist = [[float('inf')] * n for _ in range(n)]
        dist[start][start] = 0
        pq = [(0, start, start)]  # (distance, current_city, origin_city)

        while pq:
            d, curr_city, origin_city = heapq.heappop(pq)

            if d > dist[curr_city][origin_city]:
                continue

            for o, d, l in roads:
                if curr_city == o:
                    new_dist = d + dist[curr_city][origin_city]
                    if new_dist < dist[d][origin_city]:
                        dist[d][origin_city] = new_dist
                        heapq.heappush(pq, (new_dist, d, origin_city))

        return dist

    def count_paths_with_road(road_index):
        o, d, l = roads[road_index]
        count = 0
        for origin in range(n):
            if origin != o:
                dist = dijkstra(origin)
                if dist[d][origin] != float('inf'):
                    count += 1
        return count

    import heapq

    results = []
    for i in range(m):
        results.append(count_paths_with_road(i) % 1000000007)

    for result in results:
        print(result)

