    n, k = map(int, input().split())
    cards = []
    for _ in range(n):
        cards.append(tuple(map(int, input().split())))

    def calculate_waste(envelope_size, cards):
        w, h = envelope_size
        total_waste = 0
        for w_card, h_card, q in cards:
            total_waste += max(0, w - w_card) * max(0, h - h_card) * q
        return total_waste

    def find_best_envelopes(cards, k):
        import itertools

        min_waste = float('inf')
        best_combination = None

        for combination in itertools.combinations(cards, k):
            envelope_sizes = set()
            for w, h, _ in combination:
                envelope_sizes.add((max(w, h), min(w, h)))

            if len(envelope_sizes) <= k:
                total_waste = 0
                for w, h in envelope_sizes:
                    total_waste += calculate_waste((w, h), [card for card in cards if card not in combination])

                if total_waste < min_waste:
                    min_waste = total_waste
                    best_combination = combination

        return min_waste, best_combination

    min_waste, _ = find_best_envelopes(cards, k)
    print(min_waste)

solve()

