def solve():
    n, m = map(int, input().split())
    grid = [input() for _ in range(n)]

    def is_valid_placement(grid, x, y):
        if grid[x][y] == '#':
            return False
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < n and 0 <= ny < m:
                if grid[nx][ny] == 'E':
                    return False
        return True

    def place_coffee_shops(grid, x, y):
        new_grid = [list(row) for row in grid]
        new_grid[x][y] = 'E'
        return [''.join(row) for row in new_grid]

    def backtrack(grid, count):
        if count == n * m:
            return grid

        for x in range(n):
            for y in range(m):
                if grid[x][y] == '.' and is_valid_placement(grid, x, y):
                    new_grid = place_coffee_shops(grid, x, y)
                    result = backtrack(new_grid, count + 1)
                    if result:
                        return result
        return None

    result = backtrack(grid, 0)
    for row in result:
        print(row)

solve()

def solve():
    n, k = map(int, input().split())
