    rooms_with_routers = [i for i, char in enumerate(s) if char == '1']

    min_cost = n * (n + 1) // 2  # Cost of connecting all rooms directly

    for i in range(1 << len(rooms_with_routers)):
        cost = 0
        connected_rooms = set()

        for j in range(len(rooms_with_routers)):
            if (i >> j) & 1:
                cost += rooms_with_routers[j] + 1
                start = max(0, rooms_with_routers[j] - k)
                end = min(n - 1, rooms_with_routers[j] + k)
                for room in range(start, end + 1):
                    connected_rooms.add(room)

        all_rooms_connected = len(connected_rooms) == n

        if all_rooms_connected:
            min_cost = min(min_cost, cost)

    print(min_cost)

