                        if not found_umbrella:
                            fatigue += sum(umbrella[1] for umbrella in current_umbrellas)
                            break
            if i + 1 <= a:
                for umbrella in umbrellas_used:
                    if umbrella[0] == i + 1:
                        current_umbrellas.append(umbrella)
