def is_prime(n):
    if n <= 1:
        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True


def solve():
    n, m = map(int, input().split())
    matrix = []
    for _ in range(n):
        matrix.append(list(map(int, input().split())))
    ans = float('inf')
    for i in range(n):
        row = matrix[i]
        all_prime = True
        for num in row:
            if not is_prime(num):
                all_prime = False
                break
        if all_prime:
            ans = min(ans, 0)
            continue
        max_val = max(row)
        moves = max_val - min(row)
        ans = min(ans, moves)
    for j in range(m):
        col = [matrix[i][j] for i in range(n)]
        all_prime = True
        for num in col:
            if not is_prime(num):
                all_prime = False
                break
        if all_prime:
            ans = min(ans, 0)
            continue
        max_val = max(col)
        moves = max_val - min(col)
        ans = min(ans, moves)
    print(ans)


