        if length == 1:
            return 4
        if length == 2:
            return 16
        dp = [0] * (length + 1)
        dp[0] = 1
        dp[1] = 4
        dp[2] = 16
        for i in range(3, length + 1):
            dp[i] = (4 * dp[i - 1] - dp[i - 3] + MOD) % MOD
        return dp[length]

    def count_invalid_strings(length):
        if length < 3:
            return 0
        count = 0
        for i in range(length - 2):
            if 'AGC' == s[i:i + 3]:
                count = (count + 1) % MOD
        return count
    s = ['A', 'C', 'G', 'T']
