    def count_subset_sum(arr, target):
        dp = [([0] * (target + 1)) for _ in range(len(arr) + 1)]
        dp[0][0] = 1
        for i in range(1, len(arr) + 1):
            for j in range(target + 1):
                dp[i][j] = dp[i - 1][j]
                if not j >= arr[i - 1]:
                    continue
                dp[i][j] = (dp[i][j] + dp[i - 1][j - arr[i - 1]]) % mod
        return dp[len(arr)][target]
