    for i in range(n):
        for j in range(n):
            if grid[i][j] == 's':
                start = (i, j)
            elif grid[i][j] == 't':
                end = (i, j)
    if start is None or end is None:
        print('INCONCEIVABLE')
        return
    if start == end:
        print(1)
        return
    if start[0] > end[0] or start[1] > end[1]:
        print('THE GAME IS A LIE')
        return
    dp = {}
    directions = [(0, 1), (1, 0)]

    def count_paths(r, c):
        if (r, c) == end:
            return 1
        if (r, c) in dp:
            return dp[(r, c)]
        count = 0
        for dr, dc in directions:
            nr, nc = r + dr, c + dc
            if 0 <= nr < n and 0 <= nc < n and grid[nr][nc] != '#':
                count = (count + count_paths(nr, nc)) % (10 ** 9 + 7)
        dp[(r, c)] = count
        return count
    result = count_paths(start[0], start[1])
    if result == 0:
        print('THE GAME IS A LIE')
    else:
        print(result)


