    ways = {}

    def calculate_min_cost_and_ways(node, visited):
        if tuple(visited) in dp:
            return dp[tuple(visited)], ways[tuple(visited)]

        if len(visited) == n:
            return 0, 1

        min_cost = float('inf')
        num_ways = 0

        for neighbor in range(n):
            if neighbor not in visited:
                new_visited = visited + (neighbor,)
                cost, ways_count = calculate_min_cost_and_ways(neighbor, new_visited)
                total_cost = costs[neighbor] + cost
                if total_cost < min_cost:
                    min_cost = total_cost
                    num_ways = ways_count
                elif total_cost == min_cost:
                    num_ways = (num_ways + ways_count) % 1000000007

        dp[tuple(visited)] = min_cost
        ways[tuple(visited)] = num_ways
        return min_cost, num_ways

    min_cost, num_ways = calculate_min_cost_and_ways(-1, ())
