def solve():
    n, m = map(int, input().split())
    changes = []
    for _ in range(m):
        changes.append(list(map(int, input().split())))
    max_mean = 0
    for i in range(1 << m):
        current_array = [0] * n
        for j in range(m):
            if i >> j & 1:
                x, d = changes[j]
                for k in range(n):
