    if start_row == -1:
        print('IMPOSSIBLE')
        return
    directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]  # right, down, left, up
    possible_moves = []
    for dr, dc in directions:
        row = start_row
        col = start_col
        moves = []
        for _ in range(k):
            row_new = row + dr
            col_new = col + dc
            if 0 <= row_new < n and 0 <= col_new < m and grid[row_new][col_new
                ] != '*':
                moves.append((dr, dc))
                row = row_new
                col = col_new
            else:
                break
        if len(moves) == k:
            possible_moves.append(moves)
    if not possible_moves:
        print('IMPOSSIBLE')
        return
    best_moves = None
    for moves in possible_moves:
        moves_str = ''
        for dr, dc in moves:
            if dr == 1:
                moves_str += 'D'
            elif dr == -1:
