def solve():
    n, k = map(int, input().split())
    w = list(map(int, input().split()))
    mod = 10 ** 9 + 7

    def calculate_weight(subsets):
        total_weight = 0
        for subset in subsets:
            subset_weight = sum(w[i - 1] for i in subset)
            subset_size = len(subset)
            total_weight = (total_weight + subset_size * subset_weight) % mod
        return total_weight

    def count_partitions(n, k):
        if k == 1:
            return 1
        if k == n:
            return 1
        if k > n:
            return 0
        dp = {}
        def count_partitions_recursive(index, remaining_k, current_subset):
            if remaining_k == 0:
                if index == n + 1:
                    return 1
                else:
                    return 0
            if index > n:
                return 0
            if (index, remaining_k, tuple(sorted(current_subset))) in dp:
                return dp[(index, remaining_k, tuple(sorted(current_subset)))]
            ways = 0
            ways = (ways + count_partitions_recursive(index + 1, remaining_k, current_subset)) % mod
            if index not in current_subset:
                ways = (ways + count_partitions_recursive(index + 1, remaining_k - 1, current_subset | {index})) % mod
            dp[(index, remaining_k, tuple(sorted(current_subset)))] = ways
            return ways
        return count_partitions_recursive(1, k, set())
    num_partitions = count_partitions(n, k)
    total_weight = 0
    for i in range(1 << n):
        subsets = []
        current_subset = set()
        for j in range(n):
            if i >> j & 1:
                current_subset.add(j + 1)
        if len(current_subset) > 0:
            subsets.append(current_subset)
        if len(subsets) == k:
            total_weight = (total_weight + calculate_weight(subsets)) % mod
    print(total_weight)

solve()

