def find_path(start, end, grid):
    queue = [start]
    visited = {start}
    parent = {start: None}
    while queue:
        curr = queue.pop(0)
        if curr == end:
            break
        x, y, z = curr
        neighbors = [(x + 1, y, z), (x, y + 1, z), (x, y, z + 1)]
        for nx, ny, nz in neighbors:
            if 0 <= nx < len(grid) and 0 <= ny < len(grid[0]) and 0 <= nz < len(
                grid[0][0]) and (nx, ny, nz) in grid and (nx, ny, nz
                ) not in visited:
                queue.append((nx, ny, nz))
                visited.add((nx, ny, nz))
                parent[(nx, ny, nz)] = curr
    path = []
    curr = end
    while curr:
        path.append(curr)
        curr = parent[curr]
    path.reverse()
    return path


