def solve():
    expression = input()
    plus_count, minus_count = map(int, input().split())
    max_value = float('-inf')

    def evaluate(expr):
        stack = []
        current_number = ''
        for char in expr:
            if char.isdigit():
                current_number += char
            elif char == '(':
                stack.append(char)
            elif char == ')':
                number = int(current_number)
                current_number = ''
                while stack and stack[-1] != '(':
                    op = stack.pop()
                    if op == '+':
                        number = number + stack.pop()
                    else:
                        number = number - stack.pop()
                stack.pop()
                stack.append(number)
            elif char == '?':
                if not stack:
                    return float('-inf')
                num2 = stack.pop()
                num1 = stack.pop()
                if not stack:
                    return float('-inf')
                op = stack.pop()
                if op == '+':
                    stack.append(num1 + num2)
                else:
                    stack.append(num1 - num2)
            else:
                return float('-inf')
        if current_number:
            stack.append(int(current_number))
        result = stack[0]
        for i in range(0, len(stack), 2):
            result = result + stack[i + 1]
        return result
    import itertools
    for ops in itertools.permutations(['+', '-'] * (plus_count + minus_count)):
        current_expr = expression
        op_index = 0
        for i in range(len(ops)):
            current_expr = current_expr[:2 * i + 1] + ops[i] + current_expr[
                2 * i + 1:]
        value = evaluate(current_expr)
        max_value = max(max_value, value)
    print(max_value)


