                    leaves.append(neighbor)
                else:
                    leaves.extend(get_leaves(neighbor, visited | {neighbor},
                        adj))
        return leaves

    def get_adj(n, edges):
        adj = {i: [] for i in range(1, n + 1)}
        for u, v in edges:
            adj[u].append(v)
            adj[v].append(u)
        return adj

    adj = get_adj(n, edges)
    leaves = get_leaves(x, {x}, adj)
    if len(leaves) > 0:
