                            grid[r][c] = 0
                            break
                        new_grid[root_r][root_c] = 1
        grid = new_grid
        days += 1
    for _ in range(p):
        r, c = map(int, input().split())
        if grid[r][c] == 1:
            print('ALIVE')
        else:
            print(days)


