def digit_product(n):
    while n > 9:
        product = 1
        s = str(n)
        for digit in s:
            if digit != '0':
                product *= int(digit)
        n = product
    return n


def count_digits(L, R):
    counts = [0] * 9
    for i in range(L, R + 1):
        counts[digit_product(i) - 1] += 1
    return counts


