import math


def solve():
    image1 = []
    image2 = []
    for _ in range(15):
        line1, line2 = input().split()
        image1.append(line1)
        image2.append(line2)
    touches1 = []
    touches2 = []
    for y in range(15):
        for x in range(30):
            if image1[y][x] == 'X':
                touches1.append((x, y))
            if image2[y][x] == 'X':
                touches2.append((x, y))
    def calculate_grip(image):
        x_coords = [x for x, y in image]
        y_coords = [y for x, y in image]
        grip_x = sum(x_coords) / len(image)
        grip_y = sum(y_coords) / len(image)
        return grip_x, grip_y
    def calculate_touch_vector(grip, touch):
        return (touch[0] - grip[0], touch[1] - grip[1])
    def calculate_grip_spread(grip, touches):
        total_distance = 0
        for touch in touches:
            total_distance += math.sqrt((touch[0] - grip[0]) ** 2 + (touch[1] - grip[1]) ** 2)
        return total_distance / len(touches)
    def calculate_grip_rotation(grip, touches):
        total_rotation = 0
        for touch in touches:
            vector = calculate_touch_vector(grip, touch)
            total_rotation += math.atan2(vector[1], vector[0])
        return total_rotation / len(touches)
    def calculate_pan_distance(grip1, grip2):
        return math.sqrt((grip2[0] - grip1[0]) ** 2 + (grip2[1] - grip1[1]) ** 2)
    def calculate_zoom_distance(grip_spread1, grip_spread2):
        return abs(grip_spread2 - grip_spread1)
    def calculate_rotation_distance(grip_rotation):
        return abs(grip_rotation) % (2 * math.pi)
    def calculate_zoom_direction(grip_spread1, grip_spread2):
        if grip_spread2 < grip_spread1:
            return 'out'
        else:
            return 'in'
    def calculate_rotation_direction(grip_rotation):
        if grip_rotation > 0:
            return 'clockwise'
        else:
            return 'counter-clockwise'
    grip1 = calculate_grip(image1, touches1)
    grip2 = calculate_grip(image2, touches2)
    grip_spread1 = calculate_grip_spread(grip1, touches1)
    grip_spread2 = calculate_grip_spread(grip2, touches2)
    grip_rotation1 = calculate_grip_rotation(grip1, touches1)
    grip_rotation2 = calculate_grip_rotation(grip2, touches2)
    pan_distance = calculate_pan_distance(grip1, grip2)
    zoom_distance = calculate_zoom_distance(grip_spread1, grip_spread2)
    rotation_distance = calculate_rotation_distance(grip_rotation2)
    if pan_distance >= zoom_distance and pan_distance >= rotation_distance:
        print(f'{len(touches1)} pan')
    elif zoom_distance >= pan_distance and zoom_distance >= rotation_distance:
        zoom_direction = calculate_zoom_direction(grip_spread1, grip_spread2)
        print(f'{len(touches1)} zoom {zoom_direction}')
    else:
        rotation_direction = calculate_rotation_direction(grip_rotation2)
        print(f'{len(touches1)} rotate {rotation_direction}')


solve()

