def solve():
    n = int(input())
    factors = {}
    nums = []
    for _ in range(n):
        x = int(input())
        nums.append(x)
        for i in range(2, int(x ** 0.5) + 1):
            if x % i == 0:
                while x % i == 0:
                    x //= i
                if i in factors:
                    factors[i].append(len(nums) - 1)
                else:
                    factors[i] = [len(nums) - 1]
                if x in factors:
                    factors[x].append(len(nums) - 1)
                else:
                    factors[x] = [len(nums) - 1]
    if not factors:
        print(1)
        return
    counts = {}
    for num in nums:
        for prime in factors:
            if num % prime == 0:
                if prime in counts:
                    counts[prime] += 1
                else:
                    counts[prime] = 1
    if not counts:
        print(1)
        return
    prime_counts = list(counts.values())
    total_ways = 1
    mod = 10 ** 9 + 7
    for count in prime_counts:
        total_ways = (total_ways * pow(count, n, mod)) % mod
    print(total_ways)


