def preprocess_line(line):
    line = line.strip()
    if not line:
        return ''
    words = []
    words.append('')
    for char in line:
        if char == ' ':
            if words[-1]:
                words.append('')
        else:
            words[-1] += char
    result = []
    for word in words:
        if word:
            result.append(word)
    return '\n'.join(result)


