def solve():
    s = input()
    n = len(s)
    dp = {}

    def calculate_weight(factored_string):
        weight = 0
        i = 0
        while i < len(factored_string):
            if factored_string[i] == '(':
                j = i
                depth = 0
                while j < len(factored_string) and (depth > 0 or
                    factored_string[j] != ')'):
                    if factored_string[j] == '(':
                        depth += 1
                    elif factored_string[j] == ')':
                        depth -= 1
                    j += 1
                weight += j - i - 1
                i = j
            else:
                weight += 1
                i += 1
        return weight
    def get_maximal_factoring(s):
        if not s in dp:
            max_weight = float('inf')
            for i in range(1, len(s) // 2 + 1):
                if len(s) % i == 0:
                    prefix = s[:i]
                    num_repeats = len(s) // i
                    factored_prefix = '(' + prefix + ')'
                    factored_string = factored_prefix * num_repeats
                    if calculate_weight(factored_string) < max_weight:
                        max_weight = calculate_weight(factored_string)
                        dp[s] = factored_string
            if max_weight == float('inf'):
                dp[s] = s
            else:
                dp[s] = get_maximal_factoring(dp[s])
        return dp[s]
    factored_string = get_maximal_factoring(s)
    print(calculate_weight(factored_string))


