def solve():
    n, k = map(int, input().split())
    cards = []
    for _ in range(n):
        w, h, q = map(int, input().split())
        cards.append((w, h, q))
    min_waste = float('inf')
    import itertools
    for envelope_combination in itertools.combinations(cards, k):
        total_waste = 0
        envelope_sizes = set()
        for w, h, q in envelope_combination:
            envelope_sizes.add((max(w, h), min(w, h)))
        for w, h, q in cards:
            best_envelope = None
            min_waste_for_card = float('inf')
            for size_w, size_h in envelope_sizes:
                if size_w >= w and size_h >= h:
                    waste = (size_w * size_h) - (w * h)
                    if waste < min_waste_for_card:
                        min_waste_for_card = waste
                        best_envelope = (size_w, size_h)
            total_waste += min_waste_for_card * q
        min_waste = min(min_waste, total_waste)
    print(min_waste)


solve()

