    root = 1
    q = [root]
    visited = {root}
    dist = {root: 0}
    while q:
        curr = q.pop(0)
        for neighbor in adj[curr]:
            if neighbor not in visited:
                visited.add(neighbor)
                dist[neighbor] = dist[curr] + 1
                q.append(neighbor)
    leaves = []
