def solve():
    n, k = map(int, input().split())
    s = input()
    routers = []
    cost = 0
    for i in range(n):
        if s[i] == '1':
            routers.append(i)
    if not routers:
        for i in range(1, n + 1):
            cost += i
        print(cost)
        return
    best_routers = []
    min_cost = float('inf')
    for i in range(1 << len(routers)):
        current_routers = []
        current_cost = 0
        for j in range(len(routers)):
            if i >> j & 1:
                current_routers.append(routers[j])
                current_cost += routers[j] + 1
        covered = [False] * n
        for room in current_routers:
            for l in range(max(0, room - k), min(n, room + k + 1)):
                covered[l] = True
        uncovered_cost = 0
        for i in range(n):
            if not covered[i]:
                uncovered_cost += i + 1
        total_cost = current_cost + uncovered_cost
        if total_cost < min_cost:
            min_cost = total_cost
            best_routers = current_routers
    print(min_cost)


