def solve():
    n, k = map(int, input().split())
    k -= 1
    ans = ['a'] * (n - 2)
    a = 0
    b = n - 2
    while a + b < n:
        if k >= comb(n - 2, a):
            k -= comb(n - 2, a)
            a += 1
            ans.append('b')
        else:
            b += 1
            ans.append('b')
    ans = ans + ['a'] * (n - 2 - a)
    print(''.join(ans))


def comb(n, k):
    if k < 0 or k > n:
        return 0
    if k == 0 or k == n:
        return 1
    if k > n // 2:
        k = n - k
    res = 1
    for i in range(k):
        res = res * (n - i) // (i + 1)
    return res


