def solve():
    n = int(input())
    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u, v))

    def calculate_result(arrangement):
        leaves = []
        for i in range(1, n + 1):
            if i not in [edge[0] for edge in edges]:
                leaves.append(i)

        def dfs(node, current_arrangement):
            if node in leaves:
                return current_arrangement[node - 1]

            max_result = float('-inf')
            for neighbor in [edge[1] for edge in edges if edge[0] == node]:
                result = dfs(neighbor, current_arrangement)
                max_result = max(max_result, result)
            return max_result

        return dfs(1, arrangement)

    max_result = 0
    min_result = float('inf')

    import itertools

    for arrangement in itertools.permutations(range(1, n + 1)):
        max_result = max(max_result, calculate_result(dict(zip(leaves, arrangement))))
        min_result = min(min_result, calculate_result(dict(zip(leaves, arrangement))))

    print(max_result, min_result)


