import math


def dist(x1, y1, x2, y2):
    return math.sqrt((x1 - x2)**2 + (y1 - y2)**2)


def solve():
    n = int(input())
    droplets = []
    for _ in range(n):
        x, y, vx, vy, r = map(int, input().split())
        droplets.append((x, y, vx, vy, r))

    def check_collision(d1, d2):
        x1, y1, vx1, vy1, r1 = d1
        x2, y2, vx2, vy2, r2 = d2
        dist_val = dist(x1, y1, x2, y2)
        return dist_val <= r1 + r2

    def get_new_center(d1, d2):
        x1, y1, vx1, vy1, r1 = d1
        x2, y2, vx2, vy2, r2 = d2
        area1 = math.pi * r1**2
        area2 = math.pi * r2**2
        total_area = area1 + area2
        new_x = (area1 * x1 + area2 * x2) / total_area
        new_y = (area1 * y1 + area2 * y2) / total_area
        return new_x, new_y

    def get_new_velocity(d1, d2):
        x1, y1, vx1, vy1, r1 = d1
        x2, y2, vx2, vy2, r2 = d2
        area1 = math.pi * r1**2
        area2 = math.pi * r2**2
        total_area = area1 + area2
        new_vx = (area1 * vx1 + area2 * vx2) / total_area
        new_vy = (area1 * vy1 + area2 * vy2) / total_area
        return new_vx, new_vy

    def check_new_droplet_collision(new_droplet, droplets):
        for d in droplets:
            if dist(new_droplet[0], new_droplet[1], d[0], d[1]) <= d[4] + new_droplet[4]:
                return True
        return False

    def get_new_droplet(d1, d2):
        x1, y1, vx1, vy1, r1 = d1
        x2, y2, vx2, vy2, r2 = d2
        new_x, new_y = get_new_center(d1, d2)
        new_vx, new_vy = get_new_velocity(d1, d2)
        new_r = math.sqrt((math.pi * r1**2 + math.pi * r2**2) / math.pi)
        return (new_x, new_y, new_vx, new_vy, new_r)

    def get_new_droplets(droplets):
        new_droplets = []
        for i in range(len(droplets)):
            for j in range(i + 1, len(droplets)):
                if check_collision(droplets[i], droplets[j]):
                    new_droplet = get_new_droplet(droplets[i], droplets[j])
                    if not check_new_droplet_collision(new_droplet, droplets):
                        new_droplets.append(new_droplet)
        return new_droplets

    def simulate(droplets, t):
        new_droplets = get_new_droplets(droplets)
        for new_droplet in new_droplets:
            droplets.append(new_droplet)
        for i in range(len(droplets)):
            droplets[i] = (
                droplets[i][0] + droplets[i][2] * t,
                droplets[i][1] + droplets[i][3] * t,
                droplets[i][2],
                droplets[i][3],
                droplets[i][4])

    t = 0
    while True:
        new_droplets = get_new_droplets(droplets)
        if not new_droplets:
            break
        t += 0.001
        simulate(droplets, 0.001)

    if len(droplets) == n:
        print(n, 0.0)
    else:
        print(len(droplets), t)


