def solve():
    n = int(input())

    MOD = 10**9 + 7

    def count_valid_strings(length):
        if length == 0:
            return 1

        if length == 1:
            return 4

        if length == 2:
            return 16

        if length == 3:
            return 61

        dp = [0] * (length + 1)
        dp[0] = 1
        dp[1] = 4
        dp[2] = 16

        for i in range(3, length + 1):
            dp[i] = (4 * dp[i-1] - dp[i-3] + MOD) % MOD

        return dp[length]

    total_strings = pow(4, n, MOD)

    invalid_strings = 0

    for i in range(n - 2):
        if i + 3 <= n:
            invalid_strings = (invalid_strings + count_valid_strings(i + 3)) % MOD

    print((total_strings - invalid_strings) % MOD)


