def solve():
    m = int(input())

    def count_ways(n):
        ways = 0
        for a in range(1, n + 1):
            if a > n:
                break
            for b in range(a, n + 1):
                if a * b > n:
                    break
                for c in range(b, n + 1):
                    if a * b * c > n:
                        break
                    d = n // (a * b * c)
                    if a * b * c * d == n:
                        ways += 1
        return ways

    n = 1
    while True:
        if count_ways(n) == m:
            print(n)
            return
        elif count_ways(n) > m:
            print(-1)
            return
        n += 1


