        if a1 == 2 and a2 == 2:
            if y1 == y2:
                return False
            if y1 > y2:
                return False

        return True

    def find_max_lifts(pylons):
        n = len(pylons)
        adj = [[] for _ in range(n)]

        for i in range(n):
            for j in range(n):
                if i != j and is_valid_connection(pylons[i], pylons[j]):
                    adj[i].append(j)

        visited = [False] * n
        max_lifts = 0

        def dfs(node, path):
            nonlocal max_lifts
            visited[node] = True
            max_lifts = max(max_lifts, len(path))

            for neighbor in adj[node]:
                if not visited[neighbor]:
                    dfs(neighbor, path + [neighbor])

        for i in range(n):
            if not visited[i]:
                dfs(i, [i])

        return max_lifts

    print(find_max_lifts(pylons))


