def solve():
    n, p, k = map(int, input().split())
    edges = []
    for _ in range(p):
        a, b, c = map(int, input().split())
        edges.append((a, b, c))
        edges.append((b, a, c))

    def calculate_max_flow():
        graph = [[] for _ in range(n + 1)]
        for u, v, c in edges:
            graph[u].append((v, c))
            graph[v].append((u, c))

        def bfs(start, end):
            dist = [-1] * (n + 1)
            dist[start] = 0
            queue = [start]
            parent = [-1] * (n + 1)

            while queue:
                u = queue.pop(0)
                for v, capacity in graph[u]:
                    if dist[v] == -1 and capacity > 0:
                        dist[v] = dist[u] + 1
                        parent[v] = u
                        queue.append(v)
            return dist[end] != -1

        def dfs(u, end, flow):
            if u == end:
                return flow

            for i in range(len(graph[u])):
                v, capacity = graph[u][i]
                if capacity > 0 and dist[v] == dist[u] + 1:
                    new_flow = dfs(v, end, min(flow, capacity))
                    if new_flow > 0:
                        graph[u][i] = (v, capacity - new_flow)
                        graph[v][graph[v].index((u, capacity))] = (u, capacity + new_flow)
                        return new_flow
            return 0

        max_flow = 0
        while bfs(1, n):
            flow = dfs(1, n, float('inf'))
            max_flow += flow
        return max_flow

    print(calculate_max_flow())

    for _ in range(k):
        a, b, c = map(int, input().split())
        edges.append((a, b, c))
        edges.append((b, a, c))
        print(calculate_max_flow())


