    ways = {}

    def calculate_cost(junction, visited):
        if (junction, tuple(visited)) in dp:
            return dp[(junction, tuple(visited))]

        if len(visited) == n:
            return 0

        min_cost = float('inf')
        min_ways = 0

        for next_junction in range(1, n + 1):
            if next_junction not in visited:
                new_visited = visited[:]
                new_visited.append(next_junction)

                cost = calculate_cost(next_junction, new_visited)

                if cost < min_cost:
                    min_cost = cost
                    min_ways = 1
                elif cost == min_cost:
                    min_ways = (min_ways + 1) % 1000000007

        dp[(junction, tuple(visited))] = min_cost + costs[junction - 1]
        return dp[(junction, tuple(visited))]

    def calculate_ways(junction, visited):
        if (junction, tuple(visited)) in ways:
            return ways[(junction, tuple(visited))]

        if len(visited) == n:
            return 1

        ways_count = 0

        for next_junction in range(1, n + 1):
            if next_junction not in visited:
                new_visited = visited[:]
                new_visited.append(next_junction)

                ways_count = (ways_count + calculate_ways(next_junction, new_visited)) % 1000000007

        ways[(junction, tuple(visited))] = ways_count
        return ways[(junction, tuple(visited))]

    min_cost = float('inf')
    min_ways = 0

    for start_junction in range(1, n + 1):
        visited = [start_junction]
        cost = calculate_cost(start_junction, visited)
        ways_count = calculate_ways(start_junction, visited)

        if cost < min_cost:
            min_cost = cost
            min_ways = ways_count
        elif cost == min_cost:
            min_ways = (min_ways + ways_count) % 1000000007

    print(min_cost, min_ways)


