    def calculate_number(row, col):
        total_numbers = n * n
        half_numbers = (total_numbers + 1) // 2

        if (row + col) % 2 == 0:
            # Even sum of coordinates
            if row <= n // 2:
                return half_numbers + (row - 1) * n + col
            else:
                return half_numbers + (n - row) * n + col
        else:
            # Odd sum of coordinates
            if row <= n // 2:
                return half_numbers + (n - row) * n + col
            else:
                return half_numbers + (row - 1) * n + col

    for row, col in queries:
        print(calculate_number(row, col))


