def calculate_probability(index, c, n):
    if index < c:
        return 0.0

    probability = 0.0
    for i in range(index - c + 1, index + 1):
        probability += calculate_probability_helper(i, c, n)

    return probability


def calculate_probability_helper(index, c, n):
    if index < c:
        return 0.0

    if index == c:
        return 1.0 / (n - c + 1)

    if index == n - 1:
        return 1.0 / (n - c + 1)

    if index == n:
        return 0.0

    probability = 0.0
    for i in range(index - c + 1, index + 1):
        probability += calculate_probability_helper(i, c, n)

    return probability


