def solve():
    n, q = map(int, input().split())
    a = list(map(int, input().split()))

    def replace(x, k):
        a[x-1] = k

    def reverse(k):
        for i in range(1 << k):
            l = (i << k) + 1
            r = (i + 1) << k
            a[l-1:r] = a[l-1:r][::-1]

    def swap(k):
        for i in range(1 << k):
            l1 = (i << k) + 1
            r1 = (i + 1) << k
            l2 = r1
            r2 = (i + 2) << k
            a[l1-1:r1], a[l2-1:r2] = a[l2-1:r2], a[l1-1:r1]

    def sum_query(l, r):
        return sum(a[l-1:r])

    for _ in range(q):
        query = input().split()
        if query[0] == '1':
            replace(int(query[1]), int(query[2]))
        elif query[0] == '2':
            reverse(int(query[1]))
        elif query[0] == '3':
            swap(int(query[1]))
        else:
            print(sum_query(int(query[1]), int(query[2])))


solve()

