    for _ in range(n):
        your_config.append(list(map(int, input().split())))

    def find_path(config, start, end):
        visited = [False] * (n + 1)
        stack = [(start, [start])]

        while stack:
            curr_room, path = stack.pop()

            if curr_room == end:
                return path

            if visited[curr_room]:
                continue

            visited[curr_room] = True

            for i in range(1, 5):
                next_room = config[curr_room - 1][i - 1]
                if next_room != 0 and not visited[next_room]:
                    stack.append((next_room, path + [next_room]))

        return None

    senior_student_path = find_path(senior_student_config, 1, n)
    your_path = find_path(your_config, 1, n)

    if senior_student_path is None:
        print('Impossible')
        return

    if your_path is None:
        print('Impossible')
        return

    senior_student_path = [str(room) for room in senior_student_path]
