    n, m, k = map(int, input().split())
    maze = []
    for _ in range(n):
        maze.append(input())

    start_row, start_col = -1, -1
    for i in range(n):
        for j in range(m):
            if maze[i][j] == 'X':
                start_row, start_col = i, j
                break
        if start_row != -1:
            break

    def is_valid(row, col):
        return 0 <= row < n and 0 <= col < m and maze[row][col] != '*'

    def find_min_cycle(start_row, start_col, k):
        min_cycle = None
        for i in range(1 << (k - 1)):
            cycle = ''
            current_row, current_col = start_row, start_col
            for j in range(k - 1):
                if (i >> j) & 1:
                    cycle += 'R'
                else:
                    cycle += 'L'

            for j in range(k - 1):
                if (i >> j) & 1:
                    cycle += 'D'
                else:
                    cycle += 'U'

            if cycle[0] == cycle[-1]:
                valid = True
                current_row, current_col = start_row, start_col
                visited = set()
                visited.add((current_row, current_col))

                for move in cycle:
                    if move == 'L':
                        new_col = current_col - 1
                        if is_valid(current_row, new_col) and (current_row, new_col) not in visited:
                            visited.add((current_row, new_col))
                            current_col = new_col
                        else:
                            valid = False
                            break
                    elif move == 'R':
                        new_col = current_col + 1
                        if is_valid(current_row, new_col) and (current_row, new_col) not in visited:
                            visited.add((current_row, new_col))
                            current_col = new_col
                        else:
                            valid = False
                            break
                    elif move == 'U':
                        new_row = current_row - 1
                        if is_valid(new_row, current_col) and (new_row, current_col) not in visited:
                            visited.add((new_row, current_col))
                            current_row = new_row
                        else:
                            valid = False
                            break
                    elif move == 'D':
                        new_row = current_row + 1
                        if is_valid(new_row, current_col) and (new_row, current_col) not in visited:
                            visited.add((new_row, current_col))
                            current_row = new_row
                        else:
                            valid = False
                            break

                if valid and (current_row, current_col) == (start_row, start_col):
                    if min_cycle is None or cycle < min_cycle:
                        min_cycle = cycle
        return min_cycle

    min_cycle = find_min_cycle(start_row, start_col, k)
    if min_cycle is not None:
        print(min_cycle)
    else:
        print('IMPOSSIBLE')


