def solve():
    n, k = map(int, input().split())
    w = list(map(int, input().split()))

    mod = 10**9 + 7

    def calculate_weight(subsets):
        total_weight = 0
        for subset in subsets:
            subset_weight = sum(w[i-1] for i in subset)
            total_weight += len(subset) * subset_weight
        return total_weight

    def generate_partitions(index, current_partition, remaining_elements):
        if index == n:
            if len(current_partition) == k:
                subsets = [[] for _ in range(k)]
                for i, subset in enumerate(current_partition):
                    for element in subset:
                        subsets[i].append(element)
                yield subsets
            return

        for i in range(1 << remaining_elements):
            subset = []
            for j in range(remaining_elements):
                if (i >> j) & 1:
                    subset.append(j + 1)
            if len(subset) > 0:
                new_partition = current_partition + [subset]
                yield from generate_partitions(
                    index + 1, new_partition, remaining_elements - len(subset))

    total_weight = 0
    for partition in generate_partitions(0, [], n):
        total_weight = (total_weight + calculate_weight(partition)) % mod

    print(total_weight)


