def solve():
    n = int(input())
    x = list(map(int, input().split()))
    edges = []
    for _ in range(n - 1):
        edges.append(list(map(int, input().split())))

    adj = [[] for _ in range(n)]
    for u, v in edges:
        adj[u-1].append(v-1)
        adj[v-1].append(u-1)

    def gcd(a, b):
        if b == 0:
            return a
        return gcd(b, a % b)

    def find_ancestors(node, parent, path):
        path.append(node)
        if node == 0:
            return [path]
        ancestors = []
        for neighbor in adj[node]:
            if neighbor != parent:
                new_path = path[:]
                ancestors.extend(find_ancestors(neighbor, node, new_path))
        return ancestors

    def calculate_gcd(path):
        if not path:
            return 0
        gcd_val = x[path[0]]
        for i in range(1, len(path)):
            gcd_val = gcd(gcd_val, x[path[i]])
        return gcd_val

    total_sum = 0
    for i in range(n):
        for j in range(i, n):
            path = find_ancestors(j, -1, [i])
            for p in path:
                total_sum = (total_sum + calculate_gcd(p)) % (10**9 + 7)

