            current_cadence = (current_crank_gear * current_back_wheel_gear) / 1000.0

            next_crank_gear = crank_gears[i-1] if i > 0 else None
            next_back_wheel_gear = back_wheel_gears[j+1] if j < m - 1 else None

            if next_crank_gear is not None:
                next_cadence = (next_crank_gear * current_back_wheel_gear) / 1000.0
                cadence_change = ((current_cadence - next_cadence) / next_cadence) * 100
                if cadence_change > p:
