            neighbors.append((x - 1, y))
        if is_valid(x + 1, y) and maze[x + 1][y] == '/':
            neighbors.append((x + 1, y))
        if is_valid(x, y - 1) and maze[x][y - 1] == '/':
            neighbors.append((x, y - 1))
        if is_valid(x, y + 1) and maze[x][y + 1] == '\\':
            neighbors.append((x, y + 1))
        return neighbors

    def bfs(start_x, start_y):
        q = [(start_x, start_y)]
        visited = set()
        visited.add((start_x, start_y))
        while q:
            x, y = q.pop(0)
            for nx, ny in get_neighbors(x, y):
                if (nx, ny) not in visited:
                    visited.add((nx, ny))
                    q.append((nx, ny))
        return visited

    def get_regions():
        regions = []
        visited = set()
        for i in range(r):
            for j in range(c):
                if (i, j) not in visited:
