                for nx, ny in get_neighbors(x, y):
                    if (nx, ny) not in visited:
                        visited.add((nx, ny))
                        q.append((nx, ny))
            return visited

        def get_regions():
            regions = []
            visited = set()
            for i in range(r):
                for j in range(c):
                    if (i, j) not in visited:
                        region = bfs(i, j)
                        regions.append(region)
                        visited.update(region)
            return regions

        regions = get_regions()
