def solve():
    n, m, k = map(int, input().split())
    layers = []
    for _ in range(n):
        layers.append(input().split())

    def get_cpu_state(layer, row, col):
        return layers[layer][row][col]

    def get_neighbors(layer, row, col):
        neighbors = []
        if layer > 0:
            neighbors.append((layer - 1, row, col))
        if layer < n - 1:
            neighbors.append((layer + 1, row, col))
        if row > 0:
            neighbors.append((layer, row - 1, col))
        if row < m - 1:
            neighbors.append((layer, row + 1, col))
        if col > 0:
            neighbors.append((layer, row, col - 1))
        if col < k - 1:
            neighbors.append((layer, row, col + 1))
        return neighbors

    def is_controlled(cpu, layer, row, col):
        neighbors = get_neighbors(layer, row, col)
        for neighbor_layer, neighbor_row, neighbor_col in neighbors:
            if get_cpu_state(neighbor_layer, neighbor_row, neighbor_col) == '1':
                return True
        return False

    def find_controlled_by(cpu, layer, row, col):
        controlled = []
        neighbors = get_neighbors(layer, row, col)
        for neighbor_layer, neighbor_row, neighbor_col in neighbors:
            if get_cpu_state(neighbor_layer, neighbor_row, neighbor_col) == '1':
                controlled.append((neighbor_layer, neighbor_row, neighbor_col))
        return controlled

    def find_all_controls(cpu, layer, row, col):
        controls = []
        controlled = find_controlled_by(cpu, layer, row, col)
        for controlled_cpu in controlled:
            controls.append(controlled_cpu)
            controls.extend(find_all_controls(controlled_cpu[0], controlled_cpu[1], controlled_cpu[2], layer, row, col))
        return controls

    critical_cpus = 0
    for layer in range(n):
        for row in range(m):
            for col in range(k):
                if get_cpu_state(layer, row, col) == '1':
                    original_controls = find_all_controls(layer, row, col, layer, row, col)

                    # Turn off the CPU
                    layers[layer][row] = layers[layer][row][:col] + '0' + layers[layer][row][col+1:]

                    # Check if any control is disrupted
                    disrupted = False
                    for original_control in original_controls:
                        if get_cpu_state(original_control[0], original_control[1], original_control[2]) == '1':
                            disrupted = True
                            break

                    # Restore the CPU
                    layers[layer][row] = layers[layer][row][:col] + '1' + layers[layer][row][col+1:]

                    if disrupted:
                        critical_cpus += 1

    print(critical_cpus)


solve()

